import React, {useEffect, useState} from "react";
import { Link } from 'react';
import NextPath from "./nextPath";

function App() {

  const [backend, setBackend] = useState([{}]);
  const [show, setShow] = useState(false);
  const [nextp, setNextp] = useState("");

  useEffect(() => {
    fetch("/path/root").then(
      response => response.json()
    ).then(
      data => {
        setBackend(data)
        console.log(data)
      }
    )
  }, [])


  return (

    <div className="App">
      {(typeof backend.children === 'undefined') ? (
        <p>Loading...</p>
      ): (
        backend.children.map((children, i) => (
          <button type="button" key={i} onClick={()=>{setShow(true) ; setNextp({children}) ; console.log(nextp)}}>{children}</button>
          // <li>
          //   <Link onClick={} to ="/path" >{children}</Link>

          // </li>
          // <p key={i}>{children}</p>
        ))

      )}
      {show && <NextPath lpath={nextp} />}
    </div>
  );
}

export default App;
