import React, {useEffect, useState} from "react";

function NextPath({lpath}) {
    const [backend, setBackend] = useState([{}]);
    const [show, setShow] = useState(false);
    const [nextp, setNextp] = useState("");

    useEffect(() => {
        if (!lpath) {
            lpath.children = "root"
        }
        fetch(`path/${lpath.children}`).then(
          response => response.json()
        ).then(
          data => {
            setBackend(data)
            console.log(data)
          }
        )
      }, [])

      function refreshPage({nextp}) {
        window.location.reload({nextp})
      }

    return (
        <div className="App">
            {(typeof backend.children === 'undefined') ? (
                <p>Loading...</p>
            ): (
                backend.children.map((children, i) => (
                    <button type="button" key={i} onClick={()=>{setShow(true) ; setNextp({children}) ; refreshPage(nextp) }}>{children}</button>
          // <li>                
                // <p key={i}>{children}</p>
                ))

            )}
            {/* {show && <NextPath lpath={nextp} />} */}
        </div>
    )
}
export default NextPath;
