import React, {useEffect, useState} from "react";
import { Link } from 'react';
import NextPath from "./nextPath";

function App() {

  const [backend, setBackend] = useState([{}]);
  const [nextp, setNextp] = useState("");
  const [paths, setPaths] = useState([])


    useEffect(() => {
      fetchdata(nextp)
    }, [nextp])

    function fetchdata(nextp) {
        if (!nextp) {
            var last_path = "root"
        } else {
            last_path = nextp.children
        }
        // console.log(last_path)
        setPaths([...paths, last_path])
        console.log(paths)
        fetch(`path/${last_path}`).then(
            response => response.json()
        ).then(
            data => {
            setBackend(data)
            // console.log(data)
            }
        )
    }

    return (
        <div className="App">
            {(typeof backend.children === 'undefined') ? (
                <p>Loading...</p>
            ): (
                backend.children.map((children, i) => (
                    <button type="button" key={i} onClick={()=>{setNextp({children}) }}>{children}</button>
                ))

            )}
            {(typeof backend.history === 'undefined') ? (
                <p>Loading...</p>
            ): (
                backend.history.map((children, i) => (
                    <p key={i}>{children}</p>
                ))

            )}
            {
              paths.map((path, i) => (
                <button type="button" key={i} onClick={()=>{setNextp({path}) }}>{path}</button>
              ))
            }
        </div>
    )
}

export default App;
